import { apiService } from "../common/api.service";
import AnswerComponent from "../components/Answer.vue";
import MessageActions from "../components/MessageActions.vue";
export default {
    name: "Message",

    props: {
        slug:{
            type: String,
            required: true
        }
    },

    components: {
        AnswerComponent,
        MessageActions

    },

    data() {
        return {
            message: {},
            loadingAnswers: false,
            answers: [],
            userHasAnswered: false,
            showForm: false,
            newAnswerBody: null,
            error: null,
            next:null,
            requestUser:null
        }
    },

    computed: {
        isOwner() {
            return this.message.author === this.requestUser;
        },
        notFound() {
            return this.message["detail"];
        }
    },

    methods: {
        setPageTitle(title) {
            document.title= title;
        },
        setRequestUser() {
            this.requestUser = window.localStorage.getItem("username");
        },
        getMessageData() {
            let endpoint = `/api/messagestext/${this.slug}/`;
            apiService(endpoint)
                .then(data => {
                  this.message = data;
                  this.userHasAnswered = data.user_has_answered;
                  this.setPageTitle(data.content); //funzione per far si che il title sia uguale al contenuto del message
                })
        },
        getMessageAnswers() {
            let endpoint = `/api/messagestext/${this.slug}/answers/`;
            if(this.next) {
               endpoint = this.next;
            }
            this.loadingAnswers = true;
            apiService(endpoint)
                .then(data => {
                    this.answers.push(...data.results);
                    this.loadingAnswers = false;
                    if (data.next) {
                        this.next = data.next;
                    } else {
                      this.next = null;
                    }
                })
        },
        onSubmit() {
          if (this.newAnswerBody) {
              let endpoint = `/api/messagestext/${this.slug}/answer/`;
              apiService(endpoint, "POST", { body: this.newAnswerBody})
                  .then(data => {

                    this.answers.unshift(data);
                  })
              this.newAnswerBody = null;
              this.showForm = false;
              this.userHasAnswered = true;
              if(this.error) {
                 this.error = null;
              }
          } else {
              this.error = "Il campo non può essere vuoto!"
          }
      },
       async deleteAnswer(answer) {
          let endpoint = `/api/answers/${answer.id}/`;
          try{
             await apiService(endpoint, "DELETE");
             //this.answers.splice(this.answers.indexOf(answer), 1);
             this.$delete(this.answers, this.answers.indexOf(answer))
             // this.userHasAnswered = false;
          }
          catch (err) {
             console.log(err);
          }
      }
    },

    created(){
       this.getMessageData();
       this.getMessageAnswers();
       this.setRequestUser();
    }
}
